window.onload=function(){var e=new Vue({el:"#download",data:{dlButtonDisabled:!1,OSChoicesAreHidden:!0,OSChoicesAreCollapsed:!0,focusedOS:""},computed:{OSChoiceGridStyleObj:function(){return{"is-hidden":this.OSChoicesAreHidden,"is-collapsed":this.OSChoicesAreCollapsed}},windowsStyleObj:function(){return{"is-focused":"windows"===this.focusedOS,"is-semi-transparent":this.focusedOS&&"windows"!==this.focusedOS}},macStyleObj:function(){return{"is-focused":"mac"===this.focusedOS,"is-semi-transparent":this.focusedOS&&"mac"!==this.focusedOS}},linuxStyleObj:function(){return{"is-focused":"linux"===this.focusedOS,"is-semi-transparent":this.focusedOS&&"linux"!==this.focusedOS}}},methods:{showOSChoices:function(){window.requestAnimationFrame(function(){e.OSChoicesAreHidden=!1,e.dlButtonDisabled=!0,e.expandOSChoices()})},expandOSChoices:function(){window.requestAnimationFrame(function(){e.OSChoicesAreCollapsed=!1})},focusOSChoice:function(e){this.focusedOS=e}}})};